-- ----------------------------
-- Table structure for `slideshow`
-- ----------------------------
DROP TABLE IF EXISTS `slideshow`;
CREATE TABLE `slideshow` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `vol` int(11) NOT NULL,
  `day` varchar(20) NOT NULL,
  `monthYear` varchar(20) NOT NULL,
  `imageUrl` varchar(200) NOT NULL,
  `saying` varchar(200) NOT NULL,
  `author` varchar(20) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of slideshow
-- ----------------------------
INSERT INTO `slideshow` VALUES ('1', '4', '18', '05/2019', 'http://192.168.43.102:8088/uploads/images/img_1558159218952.png', '伟大的科学者在实验室里的生活，是对物体和对周围一切的执着地斗争。', '居里夫人');
INSERT INTO `slideshow` VALUES ('2', '5', '19', '05/2019', 'http://192.168.43.102:8088/uploads/images/img_1558159169739.png', '我们看到的从很远星系来的光是在几百万年之前发出的。我们在看宇宙时，是在看它的过去。', '霍金');
INSERT INTO `slideshow` VALUES ('3', '6', '20', '05/2019', 'http://192.168.43.102:8088/uploads/images/img_1558351008224.png', '存活下来的物种，并不是最强的和最聪明的，而是最能适应变化的。', '达尔文');

-- ----------------------------
-- Table structure for `test_list`
-- ----------------------------
DROP TABLE IF EXISTS `test_list`;
CREATE TABLE `test_list` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `vol` int(11) NOT NULL,
  `date` varchar(20) NOT NULL,
  `theme` varchar(20) NOT NULL,
  `intro` varchar(100) NOT NULL,
  `imageUrl` varchar(200) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of test_list
-- ----------------------------
INSERT INTO `test_list` VALUES ('1', '1', '2019/05/15', '严重的水污染', '人类生存的地球事实上是个水球，它现在受到了严重的水污染威胁。', 'http://192.168.43.102:8088/uploads/images/img_1558767159354.png');
INSERT INTO `test_list` VALUES ('2', '2', '2019/05/16', '认识抗生素', '抗生素是指有细菌、真菌或其他微生物在生长时产生的能杀灭和抑制病原体的产物。', 'http://192.168.43.102:8088/uploads/images/img_1558767204654.png');
INSERT INTO `test_list` VALUES ('3', '3', '2019/05/17', '走进矿物世界', '人类的衣、食、住、行等各个方面都离不开矿物，如建筑材料、宝石、食盐等，都来自于矿物。', 'http://192.168.43.102:8088/uploads/images/img_1558767278046.png');
INSERT INTO `test_list` VALUES ('4', '4', '2019/05/18', '猛烈的台风', '台风来袭，带来丰富降水的同时，也给人们生活造成了不便。', 'http://192.168.43.102:8088/uploads/images/img_1558767319514.png');
INSERT INTO `test_list` VALUES ('5', '5', '2019/05/19', '火星探秘', '火星肉眼看去是一颗引人注目的火红色的亮星。它缓慢地穿行于众恒星之中。', 'http://192.168.43.102:8088/uploads/images/img_1558767349131.png');
INSERT INTO `test_list` VALUES ('6', '6', '2019/05/20', '聪明的大象', '大象，不仅以其巨大的体型闻名于世，同时其长长的象鼻和象牙同样让人印象深刻。生物学上，大象隶属于独特的“长鼻目”。', 'http://192.168.43.102:8088/uploads/images/img_1558858900346.jpg');

-- ----------------------------
-- Table structure for `topic_list`
-- ----------------------------
DROP TABLE IF EXISTS `topic_list`;
CREATE TABLE `topic_list` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `testId` int(11) NOT NULL,
  `number` tinyint(1) NOT NULL,
  `question` varchar(500) NOT NULL,
  `point` varchar(500) NOT NULL,
  `option1` varchar(100) NOT NULL,
  `option2` varchar(100) NOT NULL,
  `option3` varchar(100) NOT NULL,
  `optionRight` tinyint(1) NOT NULL,
  `answer` varchar(1000) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=31 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of topic_list
-- ----------------------------
INSERT INTO `topic_list` VALUES ('1', '1', '1', '地球表面大概有多少被水覆盖？', '人类休养生息的地球事实上是个\"水球\"。', '约三成', '约七成', '约九成', '2', '地球是一颗蓝色的星球，地球表面71%的面积被水覆盖。');
INSERT INTO `topic_list` VALUES ('2', '1', '2', '地表水污染主要是由什么引起的？', '水污染日益严重，成为当今最突出的环境污染问题之一。', '排放污水', '固体污染', '废气排放', '1', '地表水的污染主要是由于污水排放引起的。');
INSERT INTO `topic_list` VALUES ('3', '1', '3', '降水污染主要是由什么引起的？', '降水是地面从大气中获得的水汽凝结物。', '排放污水', '固体污染', '废气排放', '3', '降水污染主要是由大气中的废气排放造成的，包括三氧化硫、二氧化氮等。');
INSERT INTO `topic_list` VALUES ('4', '1', '4', '病原微生物属于哪种造成水污染的污染物？', '病原微生物曾经引发严重的瘟疫流行。', '生物性污染物', '化学性污染物', '物理性污染物', '1', '病原微生物属于水体生物性污染物中的一种。生物性污染至今在一些不发达国家仍是最主要的一类污染。');
INSERT INTO `topic_list` VALUES ('5', '1', '5', '以下哪项不是水污染事件？', '水污染造成了很多危害。', '骨痛病事件', '水俣病事件', '切尔诺贝利事件', '3', '骨痛病是日本铅锌矿在采矿和冶炼中排放污水，废水在河流中积累重金属\"镉\"，在人体摄入后造成骨骼畸形、剧痛，骨脆易折等问题；水俣病是日本氮肥公司排放废水肿含汞，经食物链传递后使人中毒的事件。切尔诺贝利事件是前苏联的核电站失火爆炸的事故。');
INSERT INTO `topic_list` VALUES ('6', '2', '1', '人们最先发现的抗生素是哪一种？', '这种抗生素是英国细菌学家弗莱明于1929年在培养皿中发现的。', '青霉素', '链霉素', '四环素', '1', '弗莱明在培养皿中培养细菌时，发现从空气中偶然落在培养基上的青霉菌长出的菌落周围没有细菌生长。他认为是青霉菌产生了某种化学物质，分泌到培养基里抑制了细菌的生长。这种化学物质便是最先发现的抗生素——青霉素。');
INSERT INTO `topic_list` VALUES ('7', '2', '2', '抗生素对于下列哪项引起的疾病几乎没有任何疗效。', '抗生素虽然可以治疗很多疾病，但并不是包治百病的\"万灵丹\"。', '细菌', '病毒', '肿瘤', '2', '抗生素可用于治疗大多数细菌感染性疾病，某些抗生素还具有抗肿瘤活性，用于肿瘤的化学治疗。但对于病毒和朊病毒引起的疾病，则几乎没有任何疗效。');
INSERT INTO `topic_list` VALUES ('8', '2', '3', '以下哪项不是抗生素的用途？', '除了治病之外，抗生素还能用于其他领域。', '锻炼牙齿嚼劲', '刺激植物生长', '促进家畜生长', '1', '抗生素不仅用于医疗，还应用于农业、畜牧业和食品工业等方面。在畜牧业中非治疗用途的抗生素，称为生长促进剂。有些抗生素还具有免疫抑制和刺激植物生长作用。');
INSERT INTO `topic_list` VALUES ('9', '2', '4', '抗生素的杀菌机理不包括以下哪种？', '抗生素杀菌作用主要有4种机制。', '抑制细菌细胞壁的合成', '促进细菌生长', '干扰蛋白质的合成', '2', '抗生素杀菌作用主要有4种机制，除了抑制细菌细胞壁的合成和干扰蛋白质合成，还包括与细胞的细胞膜相互作用而影响膜的渗透性，以及抑制核酸的转录和复制。');
INSERT INTO `topic_list` VALUES ('10', '2', '5', '细菌对抗生素产生抗药性的机制不包括以下哪种？', '抗生素需要在医生的指导下使用，滥用抗生素可能会造成很多问题。', '使抗生素分解或失去活性', '固定抗菌药物作用的靶点', '改变细胞的特性', '2', '细菌对抗生素产生抗药性主要有4种机制，除了使抗生素分解或失去活性和改变细胞特性，还包括使抗菌药物作用的靶点发生改变以及细菌产生药泵(efflux pumps)将进入细胞的抗生素泵出细胞。');
INSERT INTO `topic_list` VALUES ('11', '3', '1', '石英的成分是什么？', '矿物是各种地质作用形成的天然化合物或单质，比如火山作用。它们可以是固态(如石英、金刚石)、液态(如自然汞)、气态（如火山喷气中的水蒸气）或胶态(如蛋白石)。', 'SiO2', 'C', 'CaCO3', '1', '石英为二氧化硅(SiO2)，但天然矿物成分并不是完全纯的，常含有少量杂质。');
INSERT INTO `topic_list` VALUES ('12', '3', '2', '世界上最大的翡翠有多重？', '全世界最大的一块翡翠1982年在缅甸北郊丛林中被发现！', '3000千克', '33吨', '3吨', '2', '全世界最大的一块翡翠重达33吨，1982年在缅甸北郊丛林中被发现。为运送这块翡翠，专门修筑了一条130公里长的公路，在押运途中曾几次遭到强盗袭击。');
INSERT INTO `topic_list` VALUES ('13', '3', '3', '黄铁矿主要分布在我国的什么地方？', '黄铁矿常被误认为是黄金，故又称为\"愚人金\"。', '广东英德和云浮、安徽马鞍山、甘肃白银厂', '甘肃白银厂、山西中条山、长江中下游的湖北', '湖北襄阳、云南昆阳、贵州开阳', '1', '黄铁矿常被误认为是黄金，故又称为\"愚人金\"。中国黄铁矿的储量居世界前列，著名产地有广东英德和云浮、安徽马鞍山、甘肃白银厂等。');
INSERT INTO `topic_list` VALUES ('14', '3', '4', '誉为\"华北之星\"的矿物是什么品种？', '在我国，有\"华北之星\"美誉、重达236.5克拉、被发现于张家口地区万全县大麻坪的矿物是什么品种？', '橄榄石', '翡翠', '祖母绿', '1', '我国河北省张家口地区万全县大麻坪发现的橄榄石，重量236.5克拉，取名为\"华北之星\"，是我国橄榄石之最。');
INSERT INTO `topic_list` VALUES ('15', '3', '4', '我国南京\"雨花石\"是哪种矿物？', '它是在我国南京雨花台产出的。', '水晶', '玛瑙', '石英', '2', '我国玛瑙产地分布很广泛，几乎各省都有，主要有黑龙江、辽宁、湖北等地区。南京雨花台产出的\"雨花石\"就是玛瑙的一种。');
INSERT INTO `topic_list` VALUES ('16', '4', '1', '台风在哪里生成？', '产生台风的条件，主要有三个：一是比较高的温度；二是充沛的水汽；三是在南北两半球信风相遇的激荡处。', '热带海洋', '温带海洋', '寒带海洋', '1', '热带海洋符合台风产生的三个条件。据统计，产生台风的海洋，主要有菲律宾以东的海洋、中国南海、西印度群岛以及澳洲东海岸等。');
INSERT INTO `topic_list` VALUES ('17', '4', '2', '台风中心的最大风力须达到多少级？', '台风是热带气旋的一种。在气象学中，根据热带气旋的强度作了不同的分类。', '7级（＜17.1米／秒)', '8-9级(17.2-24.4米／秒)', '10-11级(24.5-32.6米／秒)', '3', '热带气旋中心的最大风力须达到10-11级才能被称为台风或飓风。');
INSERT INTO `topic_list` VALUES ('18', '4', '3', '台风眼里的天气状况一般是怎样的？', '在台风眼（台风中心大约直径为10公里的圆面积）内，因为外围的空气旋转得太厉害，外面的空气不易进到里面去，那里好象一根孤立的大管子一样。', '云淡风清', '大雨滂沱', '乌云密布', '1', '在台风眼区，空气是向下沉的，因而云消雨散，会出现暂时的晴天，夜间还能看到一颗颗闪烁的星星。');
INSERT INTO `topic_list` VALUES ('19', '4', '4', '以下哪个台风的名字是由中国命名的？', '人们制定了一套统一的台风命名系统，由亚太地区的中国、美国和越南等十四个成员组织各提供10个名字循环使用。', '杜鹃', '梅花', '玫瑰', '1', '杜鹃。中国所提供的台风名字还包括：龙王、悟空、玉兔、海燕、风神、海神、电母、海马和海棠。');
INSERT INTO `topic_list` VALUES ('20', '4', '5', '以下情况应使用何种颜色的台风预警信号？', '12小时内可能或者已经受热带气旋影响,沿海或者陆地平均风力达10级以上，或者阵风12级以上并可能持续。', '黄色', '橙色', '红色', '2', '橙色。台风预警信号共分4级，分别是蓝色、黄色、橙色和红色。不同颜色的台风预警信号的含义不同。');
INSERT INTO `topic_list` VALUES ('21', '5', '1', '火星在中国古代被叫做什么？', '火星荧荧如火，亮度常变，位置不定，令人迷惑。', '荧惑', '启明', '阿波罗', '1', '火星荧荧如火，亮度常变，位置不定，令人迷惑，所以，中国古代称火星为\"荧惑\"。而在西方古罗马的神话中，把它想象为身披盔甲浑身是血的战神\"马尔斯\"（Mars）。天文学中火星的符号是马尔斯的长枪和盾牌的组合。');
INSERT INTO `topic_list` VALUES ('22', '5', '2', '火星表面土壤中大量含有一种金属，它是什么？', '在干燥的火星表面上遍地都是红色的土壤和岩石。', '氧化铜', '氧化铁', '汞', '2', '火星土壤中含有大量氧化铁，由于长期受紫外线的照射，铁就生成了一层红色和黄色的氧化物。整个火星就是一个生了锈的世界。');
INSERT INTO `topic_list` VALUES ('23', '5', '3', '火星一年中大概有多久的时间笼罩在漫天飞舞的狂沙中？', '火星每年都要刮起让人难以想象的特大风暴。', '约二分之一', '约四分之一', '约八分之一', '2', '火星表面的尘暴，是火星大气中独有的现象，整个火星一年中有四分之一的时间都笼罩在漫天飞舞的狂沙之中。由于火星土壤含铁量甚高，导致火星尘暴染上了桔红的色彩，空气中充斥着红色尘埃，从地球上看去，犹如一片桔红色的云。');
INSERT INTO `topic_list` VALUES ('24', '5', '4', '火星的两极是由什么组成的？', '在望远镜中，火星的两极呈白色，气温都在冰点以下。这些冰域称为极冠。', '水冰', '干冰', '白色石头', '2', '科学家确认，极冠是由固态二氧化碳凝结形成的干冰。它的范围随季节有亮区和暗区的变化。火星极区一到冬季，气温下降，大气中的二氧化碳开始凝结，使得极冠加大，颜色逐渐变淡。一到夏季冰雪融化，极冠的范围也就缩小了，暗区就逐渐扩大和变暗。');
INSERT INTO `topic_list` VALUES ('25', '5', '5', '迄今为止，人们发现的火星卫星有几颗？', '1877年8月发生了数十年难逢的火星大冲，美国天文学家霍耳(Asaph Hall)发现了火星的卫星。', '一颗', '两颗', '三颗', '2', '霍耳发现的火星卫星一共有两颗，分别命名为福波斯（Phobos,火卫一）和德莫斯（Deimos,火卫二）。他们是战神的儿子，在天上驾驶着战神的战车。火卫一和火卫二差不多就在火星的赤道平面上运行。火卫一离火星中心9450千米，直径为20多千米，从火星上看，它每天西升东落两次。火卫二离火星中心大约23500千米，直径只有15千米，公转周期是30.3小时。在火星的夜空中，你可以看到“双月悬天”的奇景。 ');
INSERT INTO `topic_list` VALUES ('26', '6', '1', '以下哪项是现今亚洲陆地上最大的野生动物？', '这种动物体型高大似墙，四腿如柱，堪称“亚洲热带丛林之王”。', '亚洲象', '黑熊', '蜥蜴', '1', '亚洲象是现今亚洲陆地上最大的野生动物。它们晨昏觅食，午间休憩，隐匿在热带、亚热带丛林深处；它们嗅觉灵敏，长鼻灵巧而有力，丛林中不时传来它们低沉的吼声，一举一动都透着\"非我莫属\"的王者气派！');
INSERT INTO `topic_list` VALUES ('27', '6', '2', '以下哪项不属于现今仅剩的长鼻目动物？', '生物学上，大象属于长鼻目，因该类群拥有与众不同的长鼻子而得名。', '非洲象', '亚洲象', '美洲象', '3', '现今，长鼻目动物剩下非洲森林象、非洲草原象和亚洲象三种了，但是在哺乳动物的进化史上，长鼻目曾经是一个异常繁盛的类群，也构成了哺乳动物进化的重要类群。');
INSERT INTO `topic_list` VALUES ('28', '6', '3', '以下哪项不属于象的进化趋势？', '历史上先后出现过大大小小各色类型的长鼻类，种类繁多。', '尾巴从短到长', '体型从小到大', '象鼻从无到有', '1', '长鼻类进化的总趋势为：体型从小到大，象鼻和象牙从无到有，从短到长。');
INSERT INTO `topic_list` VALUES ('29', '6', '4', '长鼻类最早的祖先是什么？', '迄今为止，最古老的长鼻类化石在2009年发现于欧洲摩洛哥的上古新世地层，也就是恐龙时代结束不久的500万年后。', 'Eritherium', '磷灰兽', '始祖象', '1', 'Eritherium的牙齿特征表明它就是目前已知的大象最早起源，它处于近蹄类之中一个以前未知的原始阶段，并且“有蹄”。');
INSERT INTO `topic_list` VALUES ('30', '6', '5', '以下哪项是原始长鼻类和现今长鼻类之间最重要的进化环节？', '它们在第三纪登上了历史舞台，并在这个时期异常繁盛。', '恐象', '嵌齿象', '乳齿象', '3', '原始长鼻类和现今长鼻类之间最重要的进化环节——乳齿象。在第三纪，还出现了一些进化旁支：恐象、嵌齿象和美洲乳齿象。它们与乳齿象们一同活跃在第三纪的大舞台上，但是最后先后在历史的进程中彻底的灭绝。');

-- ----------------------------
-- Table structure for `user`
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `email` varchar(64) NOT NULL,
  `password` varchar(64) NOT NULL,
  `username` varchar(20) NOT NULL,
  `signature` varchar(120) NOT NULL,
  `iconUrl` varchar(200) DEFAULT NULL,
  `coverUrl` varchar(200) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=16 DEFAULT CHARSET=utf8;

